mapper.insert:
insert into tbl_board
(board_title, board_content, board_register_date, board_update_date, member_id)
VALUES(#{board_title}, #{boardContent}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, #{memberId});

mapper.select:
select board_title, board_content, board_register_date, board_update_date, m.member_id, board_id, member_region, member
from tbl_board b join tbl_member m
on b.member_id  = m.member_id;
