mapper.select:
select m.member_id, board_id, board_title, board_region, board_register_date, board_update_date, board_status, board_price, board_content, member_nickname, member_profile_path, member_profile_uuid, member_profile_original_name, member_identification, member_email, member_region, member_hi_sentence from tbl_member m join tbl_board b on m.member_id = b.member_id
AND m.member_id = #{memberId}
order by board_id desc limit #{cri2.offset}, #{cri2.rowCount}

select m.member_id, board_id, board_title, board_region, board_register_date, board_update_date, board_status, board_price, board_content, member_nickname, member_profile_path, member_profile_uuid, member_profile_original_name, member_identification, member_email, member_region, member_hi_sentence from tbl_member m join tbl_board b on m.member_id = b.member_id
AND b.board_id = #{boardId}
order by board_id desc limit #{cri2.offset}, #{cri2.rowCount}

mapper.insert:
insert into tbl_board
(board_title, board_content, board_register_date, board_update_date, member_id, board_region, board_price)
VALUES(#{boardTitle}, #{boardContent}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, #{memberId}, #{boardRegion}, #{boardPrice});

mapper.select:
